import React, { Component } from 'react';
import Counter from './counter';

class Counters extends Component{
    constructor() {
        super();
    }
    state = {
        counters: [
            { id: 1, value: 0 },
            { id: 2, value: 2 },
            { id: 3, value: 1 },
            { id: 4, value: 0 },
        ]
    };

    handleDelete(counter) {
        let counters = this.state.counters.filter(cntr => cntr.id !== counter.id);
        this.setState({counters});
    }

    render() {
        return(
            <React.Fragment>
                { this.state.counters
                    .map(counter =>
                        <Counter
                            key={counter.id}
                            value={counter.value}
                            handleDelete={this.handleDelete}
                        >
                            <h4>Counter # {counter.id}</h4>
                        </Counter>
                    )
                }
            </React.Fragment>
        )
    }
}

export default Counters;
